const int TILE_SIZE = 32;
int MAP_WIDTH = 60, MAP_HEIGHT = 20;

const int TILES_PER_ROW = 7; // Số tile mỗi hàng trong tileset

int getTile(int x, int y, const int a[]) {
    return a[y * MAP_WIDTH + x]; // Công thức lấy tile từ mảng 1D
}

SDL_Rect getTileClip(int tileID) {
    SDL_Rect clip;
    clip.x = ( (tileID - 1 ) % TILES_PER_ROW) * TILE_SIZE; // Xác định cột
    clip.y = ( (tileID - 1 ) / TILES_PER_ROW) * TILE_SIZE; // Xác định hàng
    clip.w = TILE_SIZE;
    clip.h = TILE_SIZE;
    return clip;
}

