std:: vector<const int*> MAPS = {MAP_1, MAP_2, MAP_3, MAP_4} ;

std:: vector<SDL_Rect> walls;

void generateWalls(const int* MAP) {
    for (int y = 0; y < MAP_HEIGHT ; y++) {
        for (int x = -1 ; x < MAP_WIDTH ; x++) {
                if( MAP[ y*MAP_WIDTH + x + 1] != 0) {
                SDL_Rect wall = {x * TILE_SIZE, y * TILE_SIZE, TILE_SIZE, TILE_SIZE};
                walls.push_back(wall);
            }
        }
    }
}

std::  vector<SDL_Rect> updateWalls(int dx, const int* MAP) {
    std:: vector<SDL_Rect> newWalls;
    for (int y = 0; y < MAP_HEIGHT ; y++) {
        for (int x = -1 ; x < MAP_WIDTH ; x++) {
                if( MAP[ y*MAP_WIDTH + x + 1] != 0) {
                SDL_Rect wall = {x * TILE_SIZE - dx , y * TILE_SIZE, TILE_SIZE, TILE_SIZE};
                newWalls.push_back(wall);
            }
        }
    }
    return newWalls;
}

int currentMap = 0 ;

int getCurrentMap(){
    currentMap = (currentMap + 1)%MAPS.size();
    return currentMap;
}


void debugWalls() {
    printf("Tong so tuong: %zu\n", walls.size());
    for (size_t i = 0; i < walls.size(); i++) {
        printf("Wall %zu: x=%d, y=%d\n", i, walls[i].x, walls[i].y);
    }
}


